    def showTime(self):
        if self.startCountDown:
            self.countDown -= 1
            if self.countDown == -1:
                self.startCountDown = False

        if self.startCountDown:
            text = str(self.countDown / 10) + " s"
            self.CountdownLabel.setText(text)




    def StartCueDuration(self):
        self.startCountDown = True
        if self.countDown == 0:
            self.startCountDown = False



